// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IRealEstate {
    function transferOwnership(uint256 _id, address _to) external;
}

contract Escrow {
    IRealEstate public realEstateContract;
    address public immutable seller;

    struct Transaction {
        address buyer;
        uint256 price;
        uint256 deposit;
        bool approved;
    }

    mapping(uint256 => Transaction) public transactions;

    event SaleApproved(uint256 indexed propertyId, address indexed buyer);

    constructor(address _realEstateAddress, address _seller) {
        realEstateContract = IRealEstate(_realEstateAddress);
        seller = _seller;
    }

    /// @notice Buyer deposits payment (simplified for hackathon)
    function deposit(uint256 _propertyId) external payable {
        require(msg.value >= transactions[_propertyId].price, "Insufficient funds");
        transactions[_propertyId] = Transaction(msg.sender, msg.value, msg.value, false);
    }

    /// @notice Mock AI validation + buyer approval
    function approveSale(uint256 _propertyId) external {
        require(msg.sender == transactions[_propertyId].buyer, "Not buyer");
        require(_propertyId != 666, "AI Fraud Alert"); // Mock AI check
        transactions[_propertyId].approved = true;
        emit SaleApproved(_propertyId, msg.sender);
    }

    /// @notice Finalize sale if AI and buyer approved
    function finalize(uint256 _propertyId) external {
        Transaction memory txn = transactions[_propertyId];
        require(txn.approved, "Not approved");

        // Transfer funds to seller (simplified)
        payable(seller).transfer(txn.price);

        // Transfer NFT ownership
        realEstateContract.transferOwnership(_propertyId, txn.buyer);
    }
}
